import { useEffect, useState } from 'react'

const ScoreKeeper = () => {
  const [score, setScore] = useState(0)

  return (
    <div>
      <h1>Your score is: {score}</h1>
      <button onClick={() => setScore(prevScore => prevScore + 1)}>+</button>
      <button onClick={() => setScore(prevScore => prevScore - 1)}>-</button>
    </div>
  )
}

export default ScoreKeeper;
