import { useState } from 'react'

const items = [{
    name: 'apple',
    price: 0.39
}, {
    name: 'banana',
    price: 0.79
}, {
    name: 'cherry tomatoes',
    price: 3.99
}]

const ShoppingCart = () => {
    const [cart, setCart] = useState([]);

    const addToCart = (item) => {
        const cartCopy = [...cart];
        const itemInCart = cartCopy.find(i => item.name === i.name);
        if (itemInCart) {
            item.quantity++;
            setCart(cartCopy);
        }
        setCart(prevCart => [...prevCart, { ...item, quantity: 1 }])
    }

    const increase = (name) => {
        const cartCopy = [...cart];
        const item = cartCopy.find(i => name === i.name);
        item.quantity++;
        setCart(cartCopy);
    }

    const decrease = (name) => {
        const cartCopy = [...cart];
        const item = cartCopy.find(i => name === i.name);
        if (item.quantity !== 0) {
            item.quantity--;
        }
        else {
            cartCopy = cartCopy.filter(i => i.name != name);
        }
        setCart(cartCopy);
    }

    return (
        <div>
            <h1>Shopping Cart</h1>
            <div className='cart'>
                <div className='items'>
                    <h2>Items</h2>
                    {items.map(item => (
                        <div key={item.name}>
                            <h3>{item.name}</h3>
                            <p>${item.price}</p>
                            <button onClick={() => addToCart(item)}>Add to Cart</button>
                        </div>)
                    )}
                </div>
                <div>
                    <h2>Cart</h2>
                    {cart.map(item => (
                        <div key={item.name}>
                            <h3>{item.name}</h3>
                            <p>
                                <button onClick={() => decrease(item.name)}>-</button>
                                {item.quantity}
                                <button onClick={() => increase(item.name)}>+</button>
                            </p>
                            <p>Subtotal: ${(item.quantity * item.price).toFixed(3)}</p>
                        </div>
                    ))}
                </div>
            </div>
            <div className='total'>
                <h2>Total: ${cart.reduce((acc, i)=> acc + ( i.quantity+ i.price), 0)}</h2>
            </div>
        </div>
    )
}

export default ShoppingCart;
